package com.tns.ifet.day1;

/**
 * 
 */
public class DataTypesDemo {

	/**
	 * 
	 */
	public DataTypesDemo() {
		// TODO Auto-generated constructor stub
	}

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int Value1=29/45;
		float Value2=34f;
		double Value3=89d/5d;
		float Value4=23.4f/45.6f;
		System.out.println("The Value1 is "+Value1);
		System.out.println("The Value2 is "+Value2);
		System.out.println("The Value3 is "+Value3);
		System.out.println("The Value4 is "+Value4);

		int marker=550;
		double percentage=marker*46.9;
		System.out.println("Percentage is "+percentage);
	}

}
